import axios from 'axios';
import { useEffect, useState } from 'react';
import fetchData from '../../../fetchData';
import Loading from '../../loading/big.loading/Loading';
import SmallLaoding from '../../loading/small.loading/smallLoading';
import './mainnews.scss';
import SmallNews from './SmallNews.js'

const MainNews = ()=> {
    const [news,setNews] = useState(null);
    const [isLoading,setLoading] = useState(true);
    const [mainNews,setMainNews] = useState(null);
    const [smallNewsIndex,setNewsIndex] = useState(0);
    useEffect(()=>{
        return async()=>{
            try{
                const data = await fetchData('/news/getnews');
                setNews(data);
                setMainNews(data[0])
                setLoading(false);
            }catch(err){
                console.log(err);
            }
        }
    },[])

    useEffect(()=>{
         return async ()=>{
            const restNews = document.getElementById('restNews');
            restNews.addEventListener('click',(event)=>{
            const index = event.target.classList.split(' ')[1];
            console.log(index); 
            })
        }
    })
    return (
        <>
        {
            isLoading? <SmallLaoding/>:
            <div className="mainNews">
                <div className="imageWrapper">
                    <img className= {`image`} src={mainNews.img} alt={mainNews.title} />
                </div>
                <div className="newsText">
                    <h1 className="newsTitle">
                        {mainNews.title}
                    </h1>
                    <p className="newsPara">{mainNews.paragraph}</p>
                </div>
                {
                    isLoading? <SmallLaoding/>: <SmallNews mainNews={mainNews} setMainNews={setMainNews} news = {news}/>
                }
        </div>
        }
        
        </>
    )
}

export default MainNews;
