import axios from 'axios';
import { useEffect, useState } from 'react';
import fetchData from '../../../fetchData';
import './mainnews.scss';
import News from './smallNews';
const MainNews = ()=> {
    const [news,setNews] = useState(null);
    const [isLoading,setLoading] = useState(true);

    useEffect(()=>{
        return async()=>{
            try{
                const data = await fetchData('/news/getnews');
                setNews(data);
                setLoading(false);
            }catch(err){
                console.log(err);
            }
        }
    })
    return (

        <div className="mainNews">
            <div className="imageWrapper">
                <img className='image' src="https://library.sportingnews.com/2022-05/CristianoRonaldo%20-%20cropped.jpg" alt="" />
            </div>

            <div className="newsText">
                <h1 className="newsTitle">
                    Cristiono Wants To leave
                </h1>
                <p className="newsPara">Lorem, ipsum dolor sit amet consectetur adipisicing elit. Fuga, cum. Mollitia asperiores dicta, eos quod id inventore quibusdam officiis amet, aperiam, praesentium laborum reiciendis. Tempora vel pariatur atque architecto autem.Lorem, ipsum dolor sit amet consectetur adipisicing elit. Fuga, cum. Mollitia asperiores dicta, eos quod id inventore quibusdam officiis amet, aperiam, praesentium laborum reiciendis. Tempora vel pariatur atque architecto autem.</p>
            </div>
            <div className='restNewsContainer'>
                <News news={data}/>
            </div>

            

        </div>
    )
}

export default MainNews
